package PROGRMLIST;

public class EvnOdd {

		public static void main(String[] args) {
			// TODO Auto-generated method stub
			
			int evn=80, od=3;
			
			if(evn%2==0)
			{
				System.out.println(evn+ " is even");
			}
			else
			{
				System.out.println(evn+" is odd");
			}
			
			if(od%2!=0)
			{
				System.out.println(od+ " is odd ");
			}
			else
			{
				System.out.println(od+" is even number");
			}

	}

}
