package PROGRMLIST;

public class Quadrant_2 {

	public static void main(String[] args) {
		// TODO Auto-generated method stub

        
		System.out.println("Second Quadrant * pattern is :");
		int s=5;
		for(int i=1;i<=5;i++)
		{
			for(int b=s-1;b>=i;b--)
				
			{
				System.out.print(" ");;				
			}
					
			for(int j=1;j<=i;j++)
			{
				System.out.print("*");
			}
		System.out.println();

		
		

	}

	}

}
