package PROGRMLIST;

public class Quadrant_4 {

	public static void main(String[] args) {
		// TODO Auto-generated method stub

int i,j;
		
		System.out.println("Forth Quadrant * pattern is :");
		for(i=5;i>=0;i--)
		{
			for(j=0;j<i;j++)
			{
				System.out.print(" *");
			}
			System.out.println("");
		}
	}

}
